<template>
  <div class="home">
    <div class="feature-card ">
      <router-link to="">
        <img
          src="data:image/jpeg;base64,    "
          class="featured-img"
        />
        <div class="detail">
          <h3>naruto</h3>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Sapiente
            facilis asperiores excepturi? Animi, quia tempora? Nostrum vero,
            aliquam recusandae nemo error ipsa dolores impedit, voluptatum
            provident ut est, asperiores inventore.
          </p>
        </div>
      </router-link>
    </div>

    <form @submit.prevent="searchMovies()" class="search-box">
      <input
        type="text"
        placeholder="Entrez un titre de film"
        v-model="search"
      />
      <input type="submit" value="Rechercher" class="btn btn-primary" />
    </form>
    <div class="movies-list">
      <div class="movie" v-for="movie in movies" :key="movie.imdbID">
        <router-link :to="'/movie/' + movie.imdbID">
          <div class="product-image">
            <img :src="movie.Poster" />
            <div class="type">{{ movie.Type }}</div>
          </div>
          <div class="detail">
            <p class="">{{ movie.Year }}</p>
            <h3>{{ movie.Title }}</h3>
          </div>
        </router-link>
      </div>
    </div>
  </div>
</template>

<script>
// @ is an alias to /src
import { ref } from "vue";
import env from "@/env.js";

export default {
  setup() {
    const search = ref("");
    const movies = ref([]);
    const searchMovies = () => {
      if (search.value != "") {
        fetch(`http://www.omdbapi.com/?apikey=${env.apiKey}&s=${search.value}`)
          .then((response) => response.json())
          .then((data) => {
            // console.log(data);
            movies.value = data.Search;
            search.value = "";
          });
      }
    };
    return {
      search,
      movies,
      searchMovies
    };
  }
};
</script>
